#include <iostream>#include <unistd.h>#include "Point.h"#include "Strategy.h"using namespace std;extern "C" Point* getPoint(const int M, const int N, const int* top, const int* _board, 	const int lastX, const int lastY, const int noX, const int noY){        //transform 1D _board to 2D board	int x = -1, y = -1;	int** board = new int*[M];	for(int i = 0; i < M; i++){		board[i] = new int[N];		for(int j = 0; j < N; j++){			board[i][j] = _board[i * N + j];		}	}	                //return result    clearArray(M, N, board);	return new Point(x, y);}//external clearextern "C" void clearPoint(Point* p){	delete p;	return;}void clearArray(int M, int N, int** board){	for(int i = 0; i < M; i++){		delete[] board[i];	}	delete[] board;}